import React from "react";
import profile from "../assets/profile1.png";
import About from "./personal/About";
import Experience from "./personal/Experience";
import Contact from "./personal/Contact";

const Home = () => {
  return (
    <div className="pt-32 bg-dark-blue">
      {/* Hero Section */}
      <div
        id="home"
        className="flex items-center h-screen w-full bg-dark-blue text-white justify-center pt-40 md:pt-0 mb-5"
      >
        <div className="flex flex-col md:flex-row items-center px-6 max-w-screen-lg space-y-8 md:space-y-0">
          {/* Text Content */}
          <div className="flex flex-col items-start w-full md:w-1/2 space-y-6">
            <h1
              className="text-6xl font-bold text-blue-500"
              style={{ fontFamily: "Great Vibes, cursive" }}
            >
              Welcome to Bete Qinie!
            </h1>
            <p className="text-gray-300 text-lg leading-relaxed">
              Discover the beauty of Ethiopian poetry and culture through our
              curated collection of blog posts, interactive dictionary, and more.
              Dive in to explore, learn, and celebrate the rich heritage of
              Ethiopia with us.
            </p>
          </div>

          {/* Profile Image */}
          <div className="w-full md:w-1/2 flex justify-center">
            <img
              src={profile}
              alt="Profile"
              className="rounded-full w-2/3 md:w-3/4 shadow-lg"
            />
          </div>
        </div>
      </div>
      <About />
    </div>
  );
};

export default Home;
